import 'package:ansix/ansix.dart';
import 'package:collection/collection.dart';
import 'package:test/test.dart';

void main() {
  group('AnsiGrid with multiple lines', () {
    group('fromColumns', () {
      final List<List<Object?>> columns = <List<Object?>>[
        <Object?>[
          '#',
          ...movies.mapIndexed((int i, Movie m) => i + 1),
          'Average'
        ],
        <Object?>[
          'Title',
          ...movies.map((Movie m) =>
              AnsiText(m.title, alignment: AnsiTextAlignment.center))
        ],
        <Object?>['Release Year', ...movies.map((Movie m) => m.releaseYear)],
        <Object?>[
          'IMDb Rate',
          ...movies.map((Movie m) => m.rate),
          movies.map((Movie m) => m.rate).toList(growable: false).average
        ],
      ];

      test('vertical', () {
        expect(
            AnsiGrid.fromColumns(columns, theme: verticalTheme).formattedText,
            fromColumnsVertical);
      });

      test('horizontal', () {
        expect(
            AnsiGrid.fromColumns(columns, theme: horizontalTheme).formattedText,
            fromColumnsHorizontal);
      });
    });

    group('fromRows', () {
      final List<List<Object?>> rows = <List<Object?>>[
        <Object?>['#', 'Title', 'Release Year', 'IMDb Rate'],
        ...movies.mapIndexed((int i, Movie m) {
          return <Object>[i, m.title, m.releaseYear, m.rate];
        }).toList(growable: false),
        <Object?>[
          'Average',
          '',
          '',
          movies.map((Movie m) => m.rate).toList(growable: false).average
        ],
      ];

      test('vertical', () {
        expect(AnsiGrid.fromRows(rows, theme: verticalTheme).formattedText,
            fromRowsVertical);
      });

      test('horizontal', () {
        expect(AnsiGrid.fromRows(rows, theme: horizontalTheme).formattedText,
            fromRowsHorizontal);
      });
    });
  });
}

final AnsiGridTheme horizontalTheme = AnsiGridTheme(
  border: const AnsiBorder(
    style: AnsiBorderStyle.rounded,
    type: AnsiBorderType.all,
  ),
  wrapText: true,
  wrapOptions: const WrapOptions(
    lineBreak: false,
    splitWords: false,
  ),
  orientation: AnsiOrientation.horizontal,
  keepSameWidth: false,
  headerTextTheme: AnsiTextTheme(
    foregroundColor: AnsiColor.white,
    style: const AnsiTextStyle(bold: true),
    backgroundColor: AnsiColor.deepSkyBlue7,
    alignment: AnsiTextAlignment.center,
    padding: AnsiPadding.vertical(2),
    fixedWidth: 8,
  ),
  cellTextTheme: AnsiTextTheme(
    foregroundColor: AnsiColor.cadetBlue,
    alignment: AnsiTextAlignment.center,
    padding: AnsiPadding.horizontal(1),
    fixedWidth: 12,
  ),
  footerTextTheme: AnsiTextTheme(
    foregroundColor: AnsiColor.grey0,
    style: const AnsiTextStyle(bold: true),
    alignment: AnsiTextAlignment.center,
    backgroundColor: AnsiColor.deepSkyBlue5,
    padding: AnsiPadding.horizontal(1),
  ),
);

final AnsiGridTheme verticalTheme = horizontalTheme.copyWith(
  orientation: AnsiOrientation.vertical,
  keepSameWidth: true,
);

const List<Movie> movies = <Movie>[
  Movie(title: 'The Shawshank Redemption', releaseYear: 1994, rate: 9.3),
  Movie(title: 'The Godfather', releaseYear: 1972, rate: 9.2),
  Movie(title: 'The Dark Knight', releaseYear: 2008, rate: 9.0),
  Movie(title: 'The Godfather Part II', releaseYear: 1974, rate: 9.0),
  Movie(title: '12 Angry Men', releaseYear: 1957, rate: 9.0),
  Movie(title: 'Schindler\'s List', releaseYear: 1993, rate: 9.0),
  Movie(
      title: 'The Lord of the Rings: The Return of the King',
      releaseYear: 2003,
      rate: 9.0),
  Movie(title: 'Pulp Fiction', releaseYear: 1994, rate: 8.9),
  Movie(
      title: 'The Lord of the Rings: The Fellowship of the Ring',
      releaseYear: 2001,
      rate: 8.8),
  Movie(title: 'The Good, the Bad and the Ugly', releaseYear: 1966, rate: 8.8),
];

class Movie {
  /// Shorthand constructor
  const Movie({
    required this.title,
    required this.releaseYear,
    required this.rate,
  });

  final String title;
  final int releaseYear;
  final double rate;
}

